import { useState } from "react";
import "./App.css";
import Hello from "./Hello";

const App = () => {
  const Display = ({ counter }) => (
    <div>
      {" "}
      Like Count <b>{counter}</b>
    </div>
  );

  const Button = ({ handleClick, text }) => {
    <button onClick={handleClick}>{text}</button>;
  };

  const [counter, setCounter] = useState(0);
  // setTimeout(() => setCounter(counter + 1), 1000);
  const resetCount = () => setCounter(0);
  const increaseByOne = () => setCounter(counter + 1);
  const increaseByTwo = () => setCounter(counter + 2);

  return (
    <div className="App">
      <Hello name="Andrei" age="32" />
      <Display counter={counter} />
      <Button handleClick={increaseByOne} text="+1" />
      <Button handleClick={increaseByTwo} text="+2" />
      <Button handleClick={resetCount} text="reset" />
    </div>
  );
};

export default App;
